import FormContact from "./Layout/FormContact/FormContact";
import TableContact from "./Layout/TableContact/TableContact";
import React, { useState } from "react";

const App = () => {
  const [contacts, setContacts] = useState([
    { id: 6, name: "Имя1 Фамилия ", email: "qwe1@gmail.com" },
    { id: 12, name: "Имя2 Фамилия ", email: "qwe2@gmail.com" },
    { id: 21, name: "Имя3 Фамилия ", email: "qwe3@gmail.com" },
  ]);

  const addContact = (contactName, contactEmail) => {
    const newId =
      contacts.sort((x, y) => x.id - y.id)[contacts.length - 1].id + 1;

    const item = {
      id: newId,
      name: contactName,
      email: contactEmail,
    };
    setContacts([...contacts, item]);
  };

  const deleteContact = (id) => {
    setContacts(contacts.filter((item) => item.id !== id));
  };

  return (
    <div className="container mt-3">
      <div className="card">
        <div className="card-header">
          <h1>Список контактов</h1>
        </div>
        <div className="card-body">
          <TableContact contacts={contacts} deleteContact={deleteContact} />
          <FormContact addContact={addContact} />
        </div>
      </div>
    </div>
  );
};

export default App;
